import React, { useState } from "react";
import "./index.css"; // Ensure this CSS file is imported

const App = () => {
  const [coupon, setCoupon] = useState(null);
  const [error, setError] = useState(null);

  const handleClaimCoupon = async () => {
    try {
      const response = await fetch("http://localhost:5000/claim", {
        method: "POST",
        credentials: "include", // ‚úÖ Allows cookies for IP tracking
        headers: { "Content-Type": "application/json" },
      });

      const data = await response.json();

      if (!response.ok) throw new Error(data.message); // Handle errors

      setCoupon(data.coupon);
      setError(null);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="container">
      <h1>Round-Robin Coupon Distribution</h1>
      <p>Click the button to claim your coupon.</p>
      <button className="button" onClick={handleClaimCoupon}>Claim Coupon</button>

      {coupon && <p className="success">üéâ Your coupon: {coupon}</p>}
      {error && <p className="error">‚ùå {error}</p>}
    </div>
  );
};

export default App;
