import React, { useState } from "react";
import axios from "axios";

function App() {
  const [message, setMessage] = useState("");

  const claimCoupon = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/coupons/claim");
      setMessage(res.data.message + " Code: " + res.data.coupon);
    } catch (err) {
      setMessage(err.response?.data?.message || "Error claiming coupon.");
    }
  };

  return (
    <div>
      <h1>Claim Your Coupon</h1>
      <button onClick={claimCoupon}>Claim</button>
      <p>{message}</p>
    </div>
  );
}

export default App;
