import React, { useState } from "react";

const App = () => {
  const [coupon, setCoupon] = useState(null);
  const [error, setError] = useState("");

  const handleClaimCoupon = async () => {
    try {
      const response = await fetch("http://localhost:5000/claim", {
        method: "POST",
        credentials: "include", // ‚úÖ Needed for cookies
        headers: { "Content-Type": "application/json" },
      });

      const data = await response.json();

      if (!response.ok) throw new Error(data.message || "Failed to claim coupon");

      setCoupon(data.coupon);
      setError("");
      alert(`üéâ Your coupon: ${data.coupon}`);
    } catch (error) {
      console.error("Error:", error.message);
      setError(error.message);
      alert(`‚ùå ${error.message}`);
    }
  };

  return (
    <div className="container">
      <h1>Round-Robin Coupon Distribution</h1>
      <p>Click the button to claim your coupon.</p>
      <button className="button" onClick={handleClaimCoupon}>
        Claim Coupon
      </button>
      {coupon && <p>Your Coupon Code: <strong>{coupon}</strong></p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default App;
