import React from 'react';

function App() {
  // Function to claim coupon
  const claimCoupon = async () => {
    try {
      const response = await fetch('https://coupouns-backend-j1n0z4n3w-hiral-jains-projects.vercel.app/claim', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      // Check if the response was successful (status 200-299)
      if (response.ok) {
        const data = await response.json(); // Parse JSON response
        console.log(data); // Log the response (for example, coupon data)
        // You can now update the UI or handle the response as needed
      } else {
        console.error('Failed to claim coupon:', response.statusText); // Handle failed request
      }
    } catch (error) {
      console.error('Error occurred while claiming coupon:', error); // Handle network or other errors
    }
  };

  return (
    <div className="App">
      <h1>Claim Your Coupon!</h1>
      <button onClick={claimCoupon}>Claim Coupon</button>
    </div>
  );
}

export default App;






