import React, { useState } from "react";

const App = () => {
  const [coupon, setCoupon] = useState(null);

  const handleClaimCoupon = async () => {
    try {
      const response = await fetch("http://localhost:5000/claim-coupon", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });

      const data = await response.json();
      setCoupon(data.coupon); // Assuming backend returns `{ coupon: "ABC123" }`
      alert(`üéâ Your coupon: ${data.coupon}`);
    } catch (error) {
      console.error("Error claiming coupon:", error);
      alert("‚ùå Failed to claim coupon. Try again later.");
    }
  };

  return (
    <div className="container">
      <h1>Round-Robin Coupon Distribution</h1>
      <p>Click the button to claim your coupon.</p>
      <button className="button" onClick={handleClaimCoupon}>
        Claim Coupon
      </button>
      {coupon && <p>Your Coupon Code: <strong>{coupon}</strong></p>}
      <footer>¬© 2025 Your Company</footer>
    </div>
  );
};

export default App;
